package classday2;

public class series1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(0);
		for (int i=2;i<=8;i++) {
			System.out.print(0+" ");
			for (int j=1;j<i;j++) {
				System.out.print(i*j+" ");	
			}
			System.out.println();
		}

	}

}
